import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { observable } from 'rxjs';
import { BookCreRequest } from 'src/app/book-cre-request.model';
import { UpdatebookService } from 'src/app/services/updatebook.service';

@Component({
  selector: 'app-updatebook',
  templateUrl: './updatebook.component.html',
  styleUrls: ['./updatebook.component.css']
})
export class UpdatebookComponent {
[x: string]: any;

  result : String='';
  isAccountCreate: boolean | undefined;

  constructor(public updateBookService: UpdatebookService){}

  updateBookForm=new FormGroup({
    title:new FormControl("",[Validators.required]),
    code:new FormControl("",[Validators.required]),
    publisher:new FormControl("",[Validators.required]),
    category:new FormControl("",[Validators.required]),
    content:new FormControl('content',[Validators.required]),
    price:new FormControl("",[Validators.required]),
    status:new FormControl("",[Validators.required]),
    bookId:new FormControl("",[Validators.required]),
});
createBookReq=new BookCreRequest();

updateBook(){
 // this.updateBookForm.patchValue({content: this['section'].content});
  console.log("This is data from form")
  //console.log(this.createBookForm.value);
  this.createBookReq.Title=this.Title.value;
  this.createBookReq.Code=this.Code.value;
  this.createBookReq.Price=this.Price.value;
  this.createBookReq.Category=this.Category.value;
  this.createBookReq.Publisher=this.Publisher.value;
  this.createBookReq.Code=this.Code.value;
  this.createBookReq.Status=this.Status.value;
  this.createBookReq.Content=this.Content.value;
  let bookId=this.BookId.value;
  this.updateBookService.updateBookServ(this.createBookReq,bookId).subscribe(respdata=>{
    console.log(respdata);

    if(respdata!==null){
      this.result="Book Upadted Successfully"
      this.isAccountCreate=true;
      this.updateBookForm.reset();
    }
  });

  console.log(this.BookId.value);
  console.log("Book Request");
  console.log(this.createBookReq);

}




get Title():FormControl{
  return this.updateBookForm.get("title") as FormControl;
}
get Code():FormControl{
  return this.updateBookForm.get("code") as FormControl;
}
get Publisher():FormControl{
  return this.updateBookForm.get("publisher") as FormControl;
}
get Category():FormControl{
  return this.updateBookForm.get("category") as FormControl;
}
get Content():FormControl{
  return this.updateBookForm.get("content") as FormControl;
}
get Price():FormControl{
  return this.updateBookForm.get("price") as FormControl;
}
get Status():FormControl{
  return this.updateBookForm.get("status") as FormControl;
}

get BookId():FormControl{
  return this.updateBookForm.get("bookId") as FormControl;
}
}
<app-header></app-header>


<div calss="container" >
    <br>
    <div routerLink="/authordashboard" >
        <button>Go Back To DashBoard</button>
    </div>
    <p class="text-left fs-8" [ngClass]="isAccountCreate ? 'text-success pb-1': 'text-danger'">
        {{result}}
        </p>
<form class="border p-3 rounded"  [formGroup]="updateBookForm"  (ngSubmit)="updateBook()">
    <div class="row g-0">
        <div class="col text-center p-2 mb-3 bg-light">
            <span class="fs-3">Update Book</span>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3" >
            <label for="title" class="form-label">Title</label>
        </div>
        <div class="col mb-3 " >
            <input type="text" class="form-control" id="title"formControlName="title" >
            <div class="form-text text-danger" *ngIf="Title.errors?.['required'] && (Title.touched || Title.dirty)">Book title is rquired</div>
        </div>
        <div class="col mb-3">
            <label for="code" class="form-label">Code</label>
        </div>
        <div class="col mb-3">
            <input type="text" class="form-control" id="code" formControlName="code"/>
            <div class="form-text text-danger" *ngIf="Code.errors?.['required'] && (Code.touched || Code.dirty)">Code is rquired</div>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="publisher" class="form-label">Publisher </label>
        </div>
        <div class="col mb-3">
            <input type="text" class="form-control" id="publisher" formControlName="publisher"/>
            <div class="form-text text-danger" *ngIf="Publisher.errors?.['required'] && (Publisher.touched || Publisher.dirty)">Name is rquired</div>
        </div>
        <div class="col mb-3">
            <label for="price" class="form-label">Price</label>
        </div>
        <div class="col mb-3">
            <input type="text" class="form-control" id="price" formControlName="price" />
            <div class="form-text text-danger" *ngIf="Price.errors?.['required'] && (Price.touched || Price.dirty)">Price is rquired</div>
        </div>
    </div>


    <div class="row mb-3" >
        <div class="col mb-3">
            <label for="category" class="form-label">Category</label>
        </div>
        <div class="col ps-3">
            <input type="text" class="form-control" id="category" formControlName="category" />
            <div class="form-text text-danger" *ngIf="Category.errors?.['required'] && (Category.touched || Category.dirty)">Book categoryis rquired</div>
        </div>
        <div class="col mb-3">
            <label for="bookId" class="form-label">Book ID</label>
        </div>
        <div class="col ps-3">
            <input type="text" class="form-control" id="bookId" formControlName="bookId" />
            <div class="form-text text-danger" *ngIf="Category.errors?.['required'] && (Category.touched || Category.dirty)">Book ID rquired</div>
        </div>
        </div>
        <div class="row mb-3">
        <div class="col mb-3">
            <div class="mb-3">Book Status</div>
         </div>
         <div class="col mb-3">   
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input" value="true" id="author" formControlName="status">
                <label for="author" class="form-check-label">Active</label>
            </div>
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input" value="false" id="reader" formControlName="status">
                <label for="reader" class="form-check-label">Inactive</label>
            </div>
        </div>
    </div>
    <div class="row" >
        <div class="col mb-3">
            <label for="content" class="form-label">Content</label>
        </div>
        <div class="col mb-3">
            <textarea class="form-control" id="content" formControlName="content" >{{ ['content'] }}</textarea>
            <div class="form-text text-danger" *ngIf="Content.errors?.['required'] && (Content.touched || Content.dirty)">Book content is rquired</div>
        </div>

    </div>
      <div class="row">
        <div class="col-6 offset-3">
            <div class="d-grid">
                <button type="submit" class="btn btn-primary">Update Book</button>
            </div>
        </div>
      </div>  
</form>
</div>
