import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { JwtResponse } from 'src/app/jwt-response.model';
import { LoginRequest } from 'src/app/login-request.model';
import { LoginService } from 'src/app/services/login.service';
import { TokenStorageService } from 'src/app/services/token-storage.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

      constructor(public loginservice:LoginService,private router: Router,private tokenStorage: TokenStorageService){

      }

      isLoggedIn = false;
  isLoginFailed = false;
  errorMessage = '';
  roles: string[] = [];
  ngOnInit(): void {

  }

  loginForm=new FormGroup({
    username:new FormControl("", [Validators.required,Validators.min(3),Validators.pattern("[a-zA-Z].*")]),
    email:new FormControl("",[Validators.email,Validators.required]),
    password:new FormControl("",[Validators.required,Validators.pattern("[a-zA-Z].*")])


});
loninRequest=new LoginRequest();
authStatus: string = "";
jwtResponse=new JwtResponse();
login(){

    this.loninRequest.UserName=this.UserName.value;
    this.loninRequest.Emal=this.Email.value;
    this.loninRequest.Password=this.Password.value;

    this.loginservice.loginUser(this.loninRequest).subscribe(data=>{
      console.log(data);
   //   window.sessionStorage.setItem("Authorization",resp.headers.get('Authorization')!);

   this.tokenStorage.saveToken(data.accessToken);
   this.tokenStorage.saveUser(data);

   this.isLoginFailed = false;
   this.isLoggedIn = true;
   this.roles = this.tokenStorage.getUser().roles;

    })  


  console.log(this.UserName.value);
  console.log(this.Email.value);
  console.log(this.Password.value);
    this.loginForm.reset();

}
get UserName():FormControl{
  return this.loginForm.get("username") as FormControl;
}
get Email():FormControl{
  return this.loginForm.get("email") as FormControl;
}
get Password():FormControl{
  return this.loginForm.get("password") as FormControl;
}

}
function getCookie(arg0: string) {
  throw new Error('Function not implemented.');
}

<app-header></app-header>


<div calss="container">


  <form [formGroup]= "loginForm"  (ngSubmit)="login()" class="border p-3 rounded">
    <div class="row g-0">
        <div class="col text-center p-2 mb-3 bg-light">
            <span class="fs-3">Login</span>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3" >
            <label for="username" class="form-label">User Name</label>

            <input type="text" class="form-control" id="username" formControlName="username">
            <div class="form-text text-danger" *ngIf="UserName.errors?.['required'] && (UserName.touched || UserName.dirty)"> UserName is rquired</div>
            <div class="form-text text-danger" *ngIf="UserName.errors?.['minlength'] && (UserName.touched || UserName.dirty)">UserName is too short</div> 
            <div class="form-text text-danger" *ngIf="UserName.errors?.['pattern'] && (UserName.touched || UserName.dirty)">UserName should start with alphabat</div>

        </div>

    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="email" class="form-label">Email</label>
            <input type="email" class="form-control" id="email" formControlName="email"/>
            <div class="form-text text-danger" *ngIf="Email.errors?.['required'] && (Email.touched || Email.dirty)">Email is rquired</div>
            <div class="form-text text-danger" *ngIf="Email.errors?.['email'] && (Email.touched || Email.dirty)">Email not valid</div>
        </div>
    </div>
      <div class="row mb-3" >
        <div class="col ps-3">
            <label for="password" class="form-label">Password</label>
            <input type="password" class="form-control" id="password" formControlName="password"/>
            <div class="form-text text-danger" *ngIf="Password.errors?.['required'] && (Password.touched || Password.dirty)">Password is rquired</div>
            <div class="form-text text-danger" *ngIf="Email.errors?.['email'] && (Email.touched || Email.dirty)">Password not valid "A-Z,a-Z,0-9"</div>
        </div>
      </div>
      <div class="row">
        <div class="col-6 offset-3">
            <div class="d-grid">
                <button type="submit" class="btn btn-primary">Login</button>
            </div>
        </div>
      </div>  
</form>
</div>
