import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-blockorunblock',
  templateUrl: './blockorunblock.component.html',
  styleUrls: ['./blockorunblock.component.css']
})
export class BlockorunblockComponent {

  result : String='';
  isAccountCreate: boolean | undefined;

  constructor(){}

  blockUnblockBookForm=new FormGroup({
    bookId:new FormControl("",[Validators.required]),
    block:new FormControl("",[Validators.required]),
});

blockorUnblock(){

}
get Block(): FormControl{
  return this.blockUnblockBookForm.get("block") as FormControl;
}
get BookId():FormControl{
  return this.blockUnblockBookForm.get("bookId") as FormControl;
}

}
<app-header></app-header>


<div calss="container" >
    <br>
    <div routerLink="/authordashboard" >
        <button>Go Back To DashBoard</button>
    </div>
    <p class="text-left fs-8" [ngClass]="isAccountCreate ? 'text-success pb-1': 'text-danger'">
        {{result}}
        </p>
<form class="border p-3 rounded"  [formGroup]="blockUnblockBookForm"  (ngSubmit)="blockorUnblock()">
    <div class="row g-0">
        <div class="col text-center p-2 mb-3 bg-light">
            <span class="fs-3">Block Unblock Book </span>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="bookId" class="form-label">Book Id</label>
        </div>
        <div class="col mb-3">
            <input type="text" class="form-control" id="bookId" formControlName="bookId" />
            <div class="form-text text-danger" *ngIf="BookId.errors?.['required'] && (BookId.touched || BookId.dirty)">Book ID is rquired</div>
        </div>

    </div>
    <div class="row mb-3">
        <div class="col mb-3">
            <div class="mb-3">Book Status</div>
         </div>
         <div class="col mb-3">   
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input" value="true" id="block" formControlName="block">
                <label for="block" class="form-check-label">Block</label>
            </div>
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input" value="false" id="unblock" formControlName="block">
                <label for="unblock" class="form-check-label">UnBlock</label>
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-6 offset-3">
            <div class="d-grid">
                <button type="submit" class="btn btn-primary">Create</button>
            </div>
        </div>
      </div>  
</form>
</div>
